import axios from 'axios'
import { error } from "console";

interface User {
    auth0Id?: string;
    email: string;
    name: string;
    username: string;
    password: string;
    nickname: string;
    family_name: string;
    given_name: string;
}

export const createUserValidation = validation(getSchema =>({
    body: getSchema<User>(yup.object().shape({
        auth0Id: yup.string().optional(),
        email: yup.string().required().email().min(5),
        name: yup.string().required().min(1),
        nickname: yup.string().required().min(1),
        family_name: yup.string().required().min(1),
        given_name: yup.string().required().min(1),
        password: yup.string().required().min(1),
        username: yup.string().required().min(1),
    }))
}));



