export const createMyUser = async (req: Request, res: Response) => {
    try {   

        
        const { name, email, user_id, balance } = req.body

        console.log("Create User - ", req.body)

        const newUser = await prisma.user.create({
            data: {
                auth0Id: user_id ,
                name: name,
                email: email,
            }
        })
        
   
        
        const walletUser = await prisma.wallet.create({
            data: {
                userId: newUser.id,
                balance: balance ? balance : 0.0
            },
        })

        const updatedUser = await prisma.user.update({
            where: { id: newUser.id },
            data: {
                walletId: walletUser.id,
            }
        });
        
        

        res.status(StatusCodes.CREATED).json(updatedUser)
    } catch (error) {
        console.log(error)
        console.log("Create User - ", req.body)
        res.status(500).json({ message: "Unable to create a user" })
    }

}